// src/Header.js
import React from 'react';

const Header = () => {
  return (
    <div className="jumbotron bg-dark text-white">
      <h1 className="display-4">Welcome to our News App!</h1>
      <p className="lead">
        Where information meets inspiration. Stay informed, stay inspired. Explore the latest
        headlines, discover diverse perspectives, and empower your day with knowledge. Your journey
        to a well-informed, enriching experience begins here. Happy reading!
      </p>
    </div>
  );
};

export default Header;
