// src/App.js
import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch(
          'https://newsapi.org/v2/top-headlines?country=us&apiKey=2d1dbc45c1354f3a90212c311ee8ffeb'
        );
        const data = await response.json();
        console.log(data)
        setArticles(data.articles);
      } catch (error) {
        console.error('Error fetching news:', error);
      }
    };

    fetchNews();
  }, []);

  return (
    <div className="container mt-5">
      <h1 className="text-center mb-4">News App</h1>
      <ul className="list-group">
        {articles.map((article, index) => (
          <li key={index} className="list-group-item">
            <h2>{article.title}</h2>
            <p>{article.description}</p>
            <p>
              <strong>Author:</strong> {article.author || 'Not available'}
            </p>
            <p>
              <strong>Source:</strong> {article.source.name}
            </p>
            <p>
              <strong>Published:</strong> {new Date(article.publishedAt).toLocaleString()}
            </p>
            <a
              href={article.url}
              className="btn btn-primary"
              target="_blank"
              rel="noopener noreferrer"
            >
              Read more
            </a>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
