import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';

function App() {
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const response = await fetch(
          'https://newsapi.org/v2/top-headlines?country=us&apiKey=2d1dbc45c1354f3a90212c311ee8ffeb'
        );
        const data = await response.json();
        setArticles(data.articles);
      } catch (error) {
        console.error('Error fetching news:', error);
      }
    };

    fetchNews();
  }, []);

  return (
    <div className="container-fluid mt-5 bg-dark bg-gradient text-white">
      <div className="container">
        <div className="jumbotron bg-info text-white">
          <h1 className="display-4">Welcome to our News App!</h1>
          <p className="lead">
            Where information meets inspiration. Stay informed, stay inspired. Explore the latest
            headlines, discover diverse perspectives, and empower your day with knowledge. Your
            journey to a well-informed, enriching experience begins here. Happy reading!
          </p>
        </div>
        <div className="row">
          {articles.map((article, index) => (
            <div key={index} className="col-md-4 mb-4">
              <div className="card bg-secondary text-white">
                {article.urlToImage && (
                  <img src={article.urlToImage} alt={article.title} className="card-img-top" />
                )}
                <div className="card-body">
                  <h5 className="card-title">{article.title}</h5>
                  <p className="card-text">{article.description}</p>
                  <p className="card-text">
                    <strong>Author:</strong> {article.author || 'Not available'}
                  </p>
                  <p className="card-text">
                    <strong>Source:</strong> {article.source.name}
                  </p>
                  <p className="card-text">
                    <strong>Published:</strong> {new Date(article.publishedAt).toLocaleString()}
                  </p>
                  <a
                    href={article.url}
                    className="btn btn-primary btn-sm"
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    Read more
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default App;
